 const trialTabs = () => {
        console.log('jquery loaded');
        $('.trial-tab').click(function() {
            let $this = $(this);

            if (!$this.hasClass('active')) {
                let parent = $this.parents('.trial-downloads');
                parent.find('.trial-tabcontent, .trial-tab').removeClass('active');

                $this.addClass('active');
                parent.find('.trial-tabcontent').eq($this.index()).addClass('active');
            }
        })
    }


    waitForJQuery = setInterval(function() {
        if (typeof $ != 'undefined') {

            trialTabs();
            clearInterval(waitForJQuery);
            $(".custom-jumplinks .custom-link a")[0].click();

	$('select#versionInfo').find('option').each(function() {
        var activePlatform = $(this).closest(".fake-select").parent().hasClass("active");
        if($(this).is(':selected') && activePlatform){
            $("."+$(this).val()+'-ecl').show();
            $("."+$(this).val()+'-desc').show();
            $("."+$(this).val()+'-pru').show();
            $("."+$(this).val()+'-prd').show();
            $("."+$(this).val()+'-prn').show();
    	}
    });

	$('select#versionInfo').on('change', function() {
        var selObj = $(this).closest(".fake-select").find("select#versionInfo");
        var selectedValue = $(this).val();
        var activePlatform = $(this).closest(".fake-select").parent().hasClass("active");
        selObj.find('option').each(function() {
            $("."+$(this).val()+'-ecl').hide();
            $("."+$(this).val()+'-desc').hide();
            $("."+$(this).val()+'-pru').hide();
            $("."+$(this).val()+'-prd').hide();
            $("."+$(this).val()+'-prn').hide();
        });
        $("."+selectedValue+"-ecl").show();
        $("."+selectedValue+"-desc").show();
        $("."+$(this).val()+'-pru').show();
        $("."+$(this).val()+'-prd').show();
        $("."+$(this).val()+'-prn').show();
    })


     $(".trial-downloads > .trial-tab-group > .trial-tab").each(function(){
        $(this).click(function(){
		$('select#versionInfo').find('option').each(function() {
        var activePlatform = $(this).closest(".fake-select").parent().hasClass("active");
        if($(this).is(':selected') && activePlatform){
            $("."+$(this).val()+'-ecl').show();
            $("."+$(this).val()+'-desc').show();
            $("."+$(this).val()+'-pru').show();
            $("."+$(this).val()+'-prd').show();
            $("."+$(this).val()+'-prn').show();
    	}
    });
        });
    });

	var newVersionText = "";
	if($("#newVersionText").html() != undefined && $("#newVersionText").html() != ""){
		newVersionText = $("#newVersionText").html().trim();
	}

    $('select#versionInfo').find('option').each(function() {

		var today = new Date();
        var dd = today.getDate();
        var mm = today.getMonth()+1; //January is 0!

        var yyyy = today.getFullYear();
        if(dd<10){
            dd='0'+dd;
        }
        if(mm<10){
            mm='0'+mm;
        }
        var today = mm+'/'+dd+'/'+yyyy;
		var date1 = new Date(today);
		var releaseDate = $("."+$(this).val()+'-releaseDate').html();
        //var releaseDate = "02/06/2018";
        if(releaseDate != undefined && releaseDate != ""){
			//date comparison here
			var date2 = new Date(releaseDate);
            var timeDiff = Math.abs(date2.getTime() - date1.getTime());
            var diffDays = Math.ceil(timeDiff / (1000 * 3600 * 24));

            if(diffDays <= 30){
                var currentVersionText = $(this).text() + newVersionText;
				$(this).text(currentVersionText);
            }
        }
    });
        }
    }, 100);
