let charts=$(".chart-cont canvas"),numberRollers=$(".trend-amount");const generateChart=t=>{let r=$("html").scrollTop(),e=t.offset().top+t.height()-(r+window.innerHeight);if(e<0){chartConfigId=t.attr("chart-config");new Chart(t,chartConfigurations[chartConfigId]);t.addClass("chart-drawn")}},rollNumbers=t=>{let r=$("html").scrollTop(),e=parseInt(t.html()),n=t.offset().top+100-(r+window.innerHeight);n<0&&(t.addClass("rolled"),t.rollNumber({number:e,speed:1500,space:36,fontStyle:{"font-size":"60px",color:"inherit"}}))};$(function(){charts.each((t,r)=>$(window).scroll(function(){!$(r).hasClass("chart-drawn")&&generateChart($(r))})),numberRollers.each((t,r)=>$(window).scroll(function(){!$(r).hasClass("rolled")&&rollNumbers($(r))}))});